'     and username = :APP_USER',
'     and session_id = :P30_ID',
'     and (vote_type is null or vote_type = :G_VOTER_TYPE);',
'',
'  if sql%rowcount = 0 then',
'    insert into ks_session_votes(vote',
'                               , comments',
'                               , username',
'                               , session_id',
'                               , session_num',
'                               , vote_type',
'                               , decline_vote_flag',
