prompt --application/deployment/install/install_add_email_package_body
begin
wwv_flow_api.create_install_script(
 p_id=>wwv_flow_api.id(28502260577404431721)
,p_install_id=>wwv_flow_api.id(28502259163729431718)
,p_name=>'Add Email Package Body'
,p_sequence=>70
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create or replace package body KV_email as',
'',
'g_ws_name        varchar2(255) := ''ODTUG'';  -- OWNING WORKSPACE',
'g_owning_app_id  number := 120124;      -- OWNING APPLICATION',
'',
'procedure create_email (',
'   p_template_id      in   number,',
'   p_subject          in   varchar2  default null,',
'   p_body1_text       in   varchar2  default null,',
'   p_rows_content     in   varchar2  default null,',
'   p_body2_text       in   varchar2  default null,',
'   p_action_label     in   varchar2  default null,',
'   p_action_link      in   varchar2  default null,',
'   p_allow_opt_out_yn in   varchar2  default ''N'',',
'   p_from_who         in   varchar2  default ''setting'',',
'   p_from_email       in   varchar2  default null,',
'   p_email_id         out  number,',
'   p_status           out  varchar2,',
'   p_error            out  varchar2 )',
'is',
'begin',
'',
'   p_status := ''SUCCESS'';',
'',
'   insert into KV_emails',
'      (template_id, subject, body1_text, rows_content,',
'       body2_text, action_label, action_link, allow_opt_out_yn,',
'       from_who, from_email)',
'   values',
'      (p_template_id, p_subject, p_body1_text, p_rows_content,',
'       p_body2_text, p_action_label, p_action_link, p_allow_opt_out_yn,',
'       p_from_who, p_from_email)',
'   returning id into p_email_id;',
'',
'   commit;',
'exception',
'   when others then',
'      rollback;',
'      p_status := ''FAILURE'';',
'      p_error := substr(sqlerrm,1,4000);',
'end create_email;',
'',
'procedure add_user (',
'   p_email_id      in   number,',
'   p_username      in   varchar2,',
'   p_status        out  varchar2,',
'   p_error         out  varchar2 )',
'is',
'begin',
'',
'   p_status := ''SUCCESS'';',
'',
'   insert into KV_email_affects',
'      (email_id, username,',
'       user_tz_pref, user_lang_code, user_opted_out_yn)',
'   values',
'      (p_email_id, p_username,',
'       nvl(KV_fw.get_preference_value(p_username => p_username, p_preference_name => ''TIMEZONE''),''US/Pacific''),',
'       nvl(KV_fw.get_preference_value(p_username => p_username, p_preference_name => ''LANGUAGE''),''en''),',
'       decode(KV_fw.get_preference_value(p_username => p_username, p_preference_name => ''RECEIVE_EMAILS_YN''),',
'                    ''N'',''Y'',''N'')  );',
'   commit;',
'',
'exception',
'   when others then',
'      rollback;',
'      p_status := ''FAILURE'';',
'      p_error := substr(sqlerrm,1,4000);',
'end add_user;',
'',
'procedure validate_email (',
'   p_email_address    in   varchar2,',
'   p_status           out  varchar2,',
'   p_validated_email  out  varchar2 )',
'is',
'   l_email              varchar2(4000);',
'   l_at                 number;',
'   l_dot                number;',
'   l_domain             varchar2(4000);',
'begin',
'',
'   p_status := ''invalid'';',
'',
'   l_email  := trim(p_email_address);',
'   l_at     := instr(nvl(l_email,''x''),''@'');',
'   l_domain := substr(l_email,l_at+1);',
'   l_dot    := instr(l_domain,''.'');',
'',
'   if l_at >= 2 and l_dot > 0 then',
'      l_email := trim(l_email);',
'      l_email := trim(both ''.'' from l_email);',
'      l_email := replace(l_email,'' '',null);',
'      l_email := replace(l_email,chr(10),null);',
'      l_email := replace(l_email,chr(9),null);',
'      l_email := replace(l_email,chr(13),null);',
'      l_email := replace(l_email,chr(49824),null);',
'',
'      if length(l_email) <= 255 then',
'         p_status := ''valid'';',
'         p_validated_email := l_email;',
'      end if;',
'   end if;',
'',
'end validate_email;',
'',
'function prep_for_email (',
'   p_value  in  varchar2)',
'   return varchar2',
'is',
'begin',
'   return replace(replace(p_value,''"'',''&#34;''),',
'                                  '','',''&#44;'');',
'end prep_for_email;',
'',
'function replace_in_email (',
'   p_value  in  varchar2)',
'   return varchar2',
'is',
'begin',
'   return replace(replace(replace(p_value,''&#34;'',''"''),',
'                                          ''&quot;'',''"''),',
'                                          ''&#44;'','','');',
'end replace_in_email;',
'',
'procedure send_email (',
'   p_email_id        in   number,',
'   p_status          out  varchar2,',
'   p_error           out  varchar2 )',
'is',
'   l_send_email_yn    varchar2(1);',
'   l_email_from       varchar2(255);',
'   l_email_status     varchar2(30);',
'   l_email_cnt        number      := 0;',
'',
'   l_vc_arr2          APEX_APPLICATION_GLOBAL.VC_ARR2;',
'   l_email_body       varchar2(32000);',
'   l_email_body_text  varchar2(32000);',
'   l_table            varchar2(4000);',
'   l_table_text       varchar2(4000);',
'   l_app_name         varchar2(4000);',
'   l_app_link         varchar2(4000);',
'begin',
'',
'   for c1 in (',
'      select e.subject,',
'             e.body1_text,',
'             e.rows_content,',
'             e.body2_text,',
'             e.action_label,',
'             e.action_link,',
'             e.allow_opt_out_yn,',
'             e.from_who,',
'             e.from_email,',
'             t.full_template_html,',
'             t.text_template,',
'             t.before_rows_html,',
'             t.row_template_html,',
'             t.after_rows_html,',
'             t.action_html',
'        from KV_emails e,',
'             KV_email_templates t',
'       where e.id = p_email_id',
'         and e.template_id = t.id',
'   ) loop',
'',
'      l_send_email_yn := KV_fw.get_preference_value(''SEND_EMAIL_YN'');',
'      l_email_from := KV_fw.get_preference_value(''EMAILS_FROM'');',
'',
'      -- #BODY1#, #TABLE#, #BODY2#, #ACTION#',
'',
'      l_email_body_text := c1.text_template;',
'      l_email_body      := c1.full_template_html;',
'',
'      apex_util.set_workspace (p_workspace => g_ws_name);',
'      if nvl(NV(''APP_ID''),0) != g_owning_app_id then',
'         apex_application.g_flow_id := g_owning_app_id;',
'      end if;',
'',
'      for c1 in (',
'         select substitution_value',
'           from apex_application_substitutions',
'          where workspace = g_ws_name',
'            and application_id = g_owning_app_id',
'            and substitution_string = ''APP_NAME''',
'      ) loop',
'         l_app_name := c1.substitution_value;',
'      end loop;',
'',
'      l_email_body_text := replace(l_email_body_text,''#APP_NAME#'',l_app_name);',
'      l_email_body      := replace(l_email_body,     ''#APP_NAME#'',l_app_name);',
'',
'      l_app_link := apex_mail.get_instance_url || ''f?p='' || g_owning_app_id;',
'',
'      l_email_body_text := replace(l_email_body_text,''#APP_LINK#'',l_app_link);',
'      l_email_body      := replace(l_email_body,     ''#APP_LINK#'',l_app_link);',
'',
'      l_email_body_text := replace(l_email_body_text,''#BODY1#'',c1.body1_text);',
'      l_email_body      := replace(l_email_body,     ''#BODY1#'',c1.body1_text);',
'',
'',
'      if c1.rows_content is not null then',
'         l_table      := c1.before_rows_html;',
'         l_table_text := null;',
'',
'         -- loop through each and replace into l_tr2',
'         l_vc_arr2 := APEX_UTIL.STRING_TO_TABLE(c1.rows_content,'','');',
'         for z IN 1..l_vc_arr2.count loop',
'            l_table := l_table ||',
'                       replace(replace(c1.row_template_html, ''#LABEL#'', replace_in_email(substr(l_vc_arr2(z),2,instr(l_vc_arr2(z),''"'',1,2)-2))),',
'                                                   ''#VALUE#'', replace_in_email(rtrim(substr(l_vc_arr2(z),instr(l_vc_arr2(z),''"'',1,3)+1),''"'')));',
'            if l_table_text is not null then',
'               l_table_text := l_table_text || ''',
'   '' || replace_in_email(substr(l_vc_arr2(z),2,instr(l_vc_arr2(z),''"'',1,2)-2)) ||'' : ''|| replace_in_email(rtrim(substr(l_vc_arr2(z),instr(l_vc_arr2(z),''"'',1,3))));',
'            else',
'               l_table_text := ''   '' || replace_in_email(substr(l_vc_arr2(z),2,instr(l_vc_arr2(z),''"'',1,2)-2)) ||'' : ''|| replace_in_email(rtrim(substr(l_vc_arr2(z),instr(l_vc_arr2(z),''"'',1,3)+1),''"''));',
'            end if;',
'',
'         end loop;',
'',
'         l_table := l_table||c1.after_rows_html;',
'',
'         l_email_body_text := replace(l_email_body_text,''#TABLE#'',l_table_text);',
'         l_email_body      := replace(l_email_body,     ''#TABLE#'',l_table);',
'      else',
'         l_email_body_text := replace(l_email_body_text,''#TABLE#'',null);',
'         l_email_body      := replace(l_email_body,     ''#TABLE#'',null);',
'      end if;',
'',
'',
'      l_email_body_text := replace(l_email_body_text,''#BODY2#'',c1.body2_text);',
'      l_email_body      := replace(l_email_body,     ''#BODY2#'',c1.body2_text);',
'',
'',
'      if c1.action_label is not null and c1.action_link is not null then',
'         l_email_body_text := replace(l_email_body_text,''#ACTION#'',c1.action_label||'', ''||c1.action_link);',
'         l_email_body      := replace(l_email_body,     ''#ACTION#'',replace(replace(c1.action_html,''#ACTION_LINK#'', c1.action_link),',
'                                                                                      ''#ACTION_TEXT#'', c1.action_label));',
'      else',
'         l_email_body_text := replace(l_email_body_text,''#ACTION#'',null);',
'         l_email_body      := replace(l_email_body,     ''#ACTION#'',null);',
'      end if;',
'',
'',
'      update KV_emails',
'         set email_body = l_email_body',
'       where id = p_email_id;',
'      commit;',
'',
'      for c2 in (',
'         select a.id,',
'                a.username,',
'                case when l_send_email_yn = ''N''',
'                     then ''N''',
'                     when c1.allow_opt_out_yn = ''N''',
'                     then ''Y''',
'                     else decode(user_opted_out_yn,''Y'',''N'',''Y'')',
'                     end distribute',
'           from KV_email_affects a',
'          where a.email_id = p_email_id',
'          order by username',
'      ) loop',
'',
'         if c2.distribute = ''Y'' then',
'',
'            declare',
'               l_sqlerrm  varchar2(4000);',
'            begin',
'',
'               apex_mail.send (',
'                  p_to        => c2.username,',
'                  p_from      => case when c1.from_who = ''send_to'' then c2.username when c1.from_who = ''setting'' then l_email_from else c1.from_email end,',
'                  p_replyto   => null,',
'                  p_subj      => c1.subject,',
'                  p_body      => l_email_body_text,',
'                  p_body_html => l_email_body );',
'',
'               update KV_email_affects',
'                  set email_sent_on = localtimestamp',
'                where c2.id = id;',
'               commit;',
'',
'               l_email_cnt := l_email_cnt + 1;',
'',
'            exception',
'               when others then',
'                  l_sqlerrm := substr(sqlerrm,1,4000);',
'                  update KV_email_affects',
'                     set email_error = l_sqlerrm',
'                   where c2.id = id;',
'                  commit;',
'            end;',
'',
'         end if;',
'',
'      end loop;',
'',
'      if l_email_cnt > 0 then',
'         apex_mail.push_queue;',
'      end if;',
'',
'      update KV_emails',
'         set nbr_emails_sent = l_email_cnt',
'       where id = p_email_id;',
'',
'      if l_send_email_yn = ''N'' then',
'         update KV_emails',
'            set error_message = ''Emails created but not sent because SEND_EMAIL_YN is set to N''',
'          where id = p_email_id;',
'      end if;',
'',
'      commit;',
'',
'   end loop;',
'',
'exception',
'   when others then',
'      rollback;',
'      p_status := ''FAILURE'';',
'      p_error  := substr(sqlerrm,1,4000);',
'end send_email;',
'',
'procedure send (',
'   p_email_addresses   in   varchar2,',
'   p_template_id       in   number,',
'   p_subject           in   varchar2,',
'   p_body1_text        in   varchar2  default null,',
'   p_rows_content      in   varchar2  default null,',
'   p_body2_text        in   varchar2  default null,',
'   p_action_label      in   varchar2  default null,',
'   p_action_link       in   varchar2  default null,',
'   p_allow_opt_out_yn  in   varchar2  default ''N'',',
'   p_from_who          in   varchar2  default ''setting'',',
'   p_from_email        in   varchar2  default null,',
'   p_status            out  varchar2,',
'   p_error_code        out  varchar2 )',
'is',
'   l_found              varchar2(1) := ''N'';',
'   l_email_from         varchar2(255);',
'   l_emails             wwv_flow_global.vc_arr2;',
'   l_email              varchar2(4000);',
'   l_email_status       varchar2(30);',
'',
'   l_email_id           number;',
'',
'   l_error              varchar2(4000);',
'   custom_error         exception;',
'   deep_error           exception;',
'   l_sqlerrm            varchar2(4000);',
'begin',
'',
'   for c1 in (',
'      select 1',
'        from KV_email_templates',
'       where id = p_template_id',
'   ) loop',
'',
'      l_found := ''Y'';',
'',
'      if p_from_who = ''setting'' then',
'         l_email_from := KV_fw.get_preference_value(''EMAILS_FROM'');',
'      end if;',
'',
'      if p_from_who in (''setting'',''from_email'') then',
'         if nvl(l_email_from,p_from_email) is null then',
'            p_error_code := ''EMAIL_ERROR_NO_EMAILS_FROM'';',
'            raise custom_error;',
'         end if;',
'',
'         validate_email (',
'            p_email_address   => nvl(l_email_from,p_from_email),',
'            p_status          => l_email_status,',
'            p_validated_email => l_email_from );',
'',
'         if l_email_status = ''invalid'' then',
'            p_error_code := ''EMAIL_ERROR_INVALID_SEND_FROM'';',
'            raise custom_error;',
'         end if;',
'      end if;',
'',
'      create_email (',
'         p_template_id      => p_template_id,',
'         p_subject          => p_subject,',
'         p_body1_text       => p_body1_text,',
'         p_rows_content     => p_rows_content,',
'         p_body2_text       => p_body2_text,',
'         p_action_label     => p_action_label,',
'         p_action_link      => p_action_link,',
'         p_allow_opt_out_yn => p_allow_opt_out_yn,',
'         p_from_who         => p_from_who,',
'         p_from_email       => nvl(l_email_from,p_from_email),',
'         p_email_id         => l_email_id,',
'         p_status           => p_status,',
'         p_error            => l_error );',
'',
'      -- CREATE_EMAIL FAILED',
'      if p_status != ''SUCCESS'' then',
'         raise deep_error;',
'      end if;',
'',
'      -- GET EMAIL ADDRESSES, ONLY VALID AND NON-REPEATED ONES',
'      l_emails := wwv_flow_utilities.string_to_table(p_email_addresses,'','');',
'',
'      for j in 1..l_emails.count loop',
'          l_email := trim(l_emails(j));',
'',
'          if l_email is not null then',
'',
'             validate_email (',
'                p_email_address   => l_email,',
'                p_status          => l_email_status,',
'                p_validated_email => l_email );',
'',
'             if l_email_status = ''valid'' then',
'',
'                for c1 in (',
'                   select 1 from dual',
'                    where not exists',
'                          (select 1',
'                             from KV_email_affects',
'                            where email_id = l_email_id',
'                              and username = lower(l_email))',
'                ) loop',
'                   add_user (',
'                      p_email_id     => l_email_id,',
'                      p_username     => lower(l_email),',
'                      p_status       => p_status,',
'                      p_error        => l_error );',
'',
'                   -- ADD_USER FAILED',
'                   if p_status != ''SUCCESS'' then',
'                      raise deep_error;',
'                   end if;',
'                end loop;',
'',
'             end if;',
'          end if;',
'          l_email := null;',
'      end loop;',
'',
'      for c2 in (',
'         select count(*) cnt',
'           from KV_email_affects',
'          where email_id = l_email_id',
'      ) loop',
'         if c2.cnt = 0 then',
'            p_error_code := ''EMAIL_ERROR_NO_VALID_EMAILS'';',
'            raise custom_error;',
'         end if;',
'      end loop;',
'',
'      send_email (',
'         p_email_id => l_email_id,',
'         p_status   => p_status,',
'         p_error    => l_error );',
'',
'      if p_status = ''FAILURE'' then',
'         raise deep_error;',
'      end if;',
'   end loop;',
'',
'   -- TEMPLATE_ID NOT FOUND',
'   if l_found = ''N'' then',
'      p_error_code := ''EMAIL_ERROR_TEMPLATE_NOT_FOUND'';',
'      raise custom_error;',
'   end if;',
'',
'exception',
'   when custom_error then',
'      rollback;',
'      p_status := ''FAILURE'';',
'      if l_email_id is not null then',
'         update KV_emails',
'            set email_status = ''FAILURE'',',
'                error_message = apex_lang.message(p_error_code)',
'          where id = l_email_id;',
'         commit;',
'      end if;',
'   when deep_error then',
'      rollback;',
'      p_status := ''FAILURE'';',
'      p_error_code := ''EMAIL_ERROR_OTHER'';',
'      if l_email_id is not null then',
'         update KV_emails',
'            set email_status = ''FAILURE'',',
'                error_message = l_error',
'          where id = l_email_id;',
'         commit;',
'      end if;',
'   when others then',
'      rollback;',
'      p_status := ''FAILURE'';',
'      p_error_code := ''EMAIL_ERROR_OTHER'';',
'      if l_email_id is not null then',
'         l_sqlerrm := substr(sqlerrm,1,4000);',
'         update KV_emails',
'            set email_status = ''FAILURE'',',
'                error_message = l_sqlerrm',
'          where id = l_email_id;',
'         commit;',
'      else',
'         KV_fw.add_error_log (p_procedure_name  => ''KV_email.send'', p_error => sqlerrm,',
'                                p_arg1_name => ''p_email_addresses'', p_arg1_val => substr(p_email_addresses,1,4000),',
'                                p_arg2_name => ''p_template_id'', p_arg2_val => p_template_id,',
'                                p_arg3_name => ''p_subject'', p_arg3_val => p_subject );',
'      end if;',
'end send;',
'',
'end KV_email;',
'/',
'',
''))
);
end;
