prompt --application/deployment/install/install_user_preference_table
begin
wwv_flow_api.create_install_script(
 p_id=>wwv_flow_api.id(28502260747865431721)
,p_install_id=>wwv_flow_api.id(28502259163729431718)
,p_name=>'User Preference Table'
,p_sequence=>80
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'create table KV_user_preferences (',
'    id                    number,',
'    row_version_number    number,',
'    user_id               number not null enable,',
'    preference_name       varchar2(100),',
'    preference_value      varchar2(4000),',
'    created               timestamp with local time zone,',
'    created_by            varchar2(255),',
'    updated               timestamp with local time zone,',
'    updated_by            varchar2(255),',
'    constraint KV_user_preferences_pk primary key (id) using index enable',
');',
'',
'alter table KV_user_preferences add constraint KV_user_pref_fk foreign key (user_id)',
'    references KV_users (id) on delete cascade enable;',
'',
'create or replace trigger biu_KV_user_preferences',
'    before insert or update on KV_user_preferences',
'    for each row',
'begin',
'    if :new.id is null then',
'        :new.id := to_number(sys_guid(), ''XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX'');',
'    end if;',
'    if inserting then',
'        :new.created := localtimestamp;',
'        :new.created_by := lower(nvl(sys_context(''APEX$SESSION'',''APP_USER''),user));',
'        :new.row_version_number := 1;',
'    elsif updating then',
'        :new.row_version_number := nvl(:old.row_version_number,1) + 1;',
'    end if;',
'    :new.updated := localtimestamp;',
'    :new.updated_by := lower(nvl(sys_context(''APEX$SESSION'',''APP_USER''),user));',
'end;',
'/',
'',
'alter trigger biu_KV_user_preferences enable;',
''))
);
end;
